    COALESCE(msg.video_p25_combined_1,0)::NUMBER(24,8) as SRC_VIDEO_P25_COMBINED_1,
    COALESCE(msg.video_p25_combined_2,0)::NUMBER(24,8) as SRC_VIDEO_P25_COMBINED_2,
    COALESCE(msg.video_p50_combined_1,0)::NUMBER(24,8) as SRC_VIDEO_P50_COMBINED_1,
    COALESCE(msg.video_p50_combined_2,0)::NUMBER(24,8) as SRC_VIDEO_P50_COMBINED_2,
    COALESCE(msg.video_p75_combined_1,0)::NUMBER(24,8) as SRC_VIDEO_P75_COMBINED_1,
    COALESCE(msg.video_p75_combined_2,0)::NUMBER(24,8) as SRC_VIDEO_P75_COMBINED_2,
    COALESCE(msg.video_p95_combined_1,0)::NUMBER(24,8) as SRC_VIDEO_P95_COMBINED_1,
    COALESCE(msg.video_p95_combined_2,0)::NUMBER(24,8) as SRC_VIDEO_P95_COMBINED_2,
    COALESCE(msg.web_sessions_1,0)::NUMBER(24,8) as SRC_WEB_SESSIONS_1,
    COALESCE(msg.web_sessions_2,0)::NUMBER(24,8) as SRC_WEB_SESSIONS_2,
    msg.file_created_ts as DATA_AS_OF_TS,
    msg.file_name as FILE_NAME,
    msg.file_row_number as FILE_ROW_NUMBER,
    msg.date_wid as DATE_WID
FROM {int_env}.third_party.MSG_DELIVERY_PINTEREST_AD_GROUP_LATEST at(timestamp => '{tt_ts}'::timestamp) msg
INNER JOIN {env}_basis_platform.core.SRC_DIRECT_DELIVERY_SOURCES at(timestamp => '{tt_ts}'::timestamp) ds
ON msg.EXTERNAL_CAMPAIGN_ID = ds.EXTERNAL_ID
    INNER JOIN {env}_basis_platform.core.SRC_DIRECT_DELIVERY_SOURCE_GROUPS at(timestamp => '{tt_ts}'::timestamp) dsg
    ON ds.DELIVERY_SOURCE_GROUP_ID = dsg.id AND dsg.ADSERVER = msg.INTEGRATION_SOURCE_CODE AND dsg.AGENCY_ID = msg.AGENCY_ID
    INNER JOIN {env}_basis_platform.core.SRC_DIRECT_DELIVERY_SOURCE_MAPPINGS at(timestamp => '{tt_ts}'::timestamp) dm
    ON dm.delivery_source_id = ds.id AND dm.deleted_at is null
    INNER JOIN {env}_basis_platform.core.dim_line_item at(timestamp => '{tt_ts}'::timestamp) li
    ON dm.mapping_ref = li.delivery_source_ref
WHERE ds.DELETED_AT is null
  AND msg.delivered_on_date >= li.START_DATE
  AND msg.delivered_on_date <= li.END_DATE
  AND li.DELETED_AT is NULL AND li.is_client_last_approved
